DROP TABLE IF EXISTS `bw_link`;
CREATE TABLE `bw_link` (
  `l_id` int(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '下载链接ID',
  `vid` int(8) NOT NULL COMMENT '数据ID',
  `l_name` varchar(60) DEFAULT NULL COMMENT '名称',
  `l_link` varchar(255) DEFAULT NULL COMMENT '原始链接地址',
  `l_type` varchar(2) DEFAULT NULL COMMENT '类型',
  `l_status` int(11) DEFAULT NULL,
  `l_createtime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `l_updatetime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`l_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

